mapper.select:
SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT a.ADMIN_ID as "adminId", a.ADMIN_NAME as "adminName", a.ADMIN_TEL as "adminTel", a.ADMIN_EMAIL as "adminEmail", a.PART_ID as "partId", case a.LOCK_YN when 'Y' then 'O' else 'X' end as lockYn, case a.Use_YN when 'Y' then 'O' else 'X' end as UseYn, DATE_FORMAT(a.REG_DATE, '%Y-%m-%d') as regDate , c.AUTHOR_NM as "deptLevel", a.EMP_NO , CASE a.ADMIN_LEVEL WHEN 'ROLE_ADMIN' THEN '관리자' ELSE (SELECT ifnull(part_nm , '없음') FROM tb_partinfo b WHERE b.PART_ID = a.PART_ID) END as partNm FROM tb_managerinfo a , lettnauthorinfo c WHERE a.ADMIN_LEVEL = c.AUTHOR_CODE
and a.ADMIN_ID LIKE CONCAT('%',#{searchKeyword},'%')
and a.ADMIN_NAME LIKE CONCAT('%',#{searchKeyword},'%')
and a.USE_YN = #{useYn}
ORDER BY a.REG_DATE DESC ) TB limit #{firstIndex} , #{recordCountPerPage}

            SELECT  ifnull(count(*),0)      
		    FROM   tb_managerinfo
		    WHERE  ADMIN_ID = #{adminId}

mapper.update:
update tb_managerinfo set ADMIN_NAME = #{adminName}
                                            ,  USE_YN = #{useYn}
                                            ,  PART_ID = #{partId }
                                            ,  ADMIN_EMAIL = #{adminEmail, jdbcType=VARCHAR }
                                            ,  ADMIN_TEL = #{adminTel, jdbcType=VARCHAR }
                                            ,  ADMIN_LEVEL = #{adminLevel , jdbcType=VARCHAR }

                                            , ADMIN_PASSWORD = #{adminPassword}
                                            , UPDATE_PASSWORD = now()
                                            , LOCK_YN ='N'


                                            ,  EMP_NO = #{empNo, jdbcType=VARCHAR }

where ADMIN_ID = #{adminId}

update tb_managerinfo set ADMIN_PASSWORD = #{adminPassword}
                    , UPDATE_PASSWORD = now()
where  ADMIN_ID = #{adminId}
