mapper.select:
SELECT TB.* FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, ROWNUM RNUM, X.* FROM ( SELECT a.LOG_ID, a.CONECT_ID, a.CONECT_IP, a.CONECT_MTHD, a.ERROR_OCCRRNC_AT, a.ERROR_CODE, CREAT_DT, b.ADMIN_NAME, c.PART_ID, c.PART_NM, CASE a.CONECT_MTHD WHEN 'I' then '로그인' ELSE '로그아웃' END CONECT_MTHDTXT FROM COMTNLOGINLOG a, TB_MANAGERINFO b, TB_PARTINFO c WHERE a.CONECT_ID = b.ADMIN_ID AND b.PART_ID = c.PART_ID
AND TO_CHAR(a.CREAT_DT, 'yyyyMMdd') >= #{searchStartdday}
AND TO_CHAR(a.CREAT_DT, 'yyyyMMdd') <= #{searchEndday}
AND b.ADMIN_NAME LIKE '%' || #{searchKeyword} || '%'
AND a.CONECT_ID LIKE '%' || #{searchKeyword} || '%'
ORDER BY a.LOG_ID DESC )X )TB WHERE TB.RNUM BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage} ORDER BY TB.LOG_ID DESC
